  {
    $path_template = dirname(dirname(dirname(__FILE__))) . DIRECTORY_SEPARATOR . 'data';
    $this->object->template_dir = $path_template;
    $json_output = $this->object->render_template('token_test.json');
    $this->assertJson($json_output);
